import algoliasearch from 'algoliasearch/lite';
import React, { Component } from 'react';
import {
  InstantSearch,
  Hits,
  SearchBox,
  Pagination,
  Highlight,
  ClearRefinements,
  RefinementList,
  Configure,
} from 'react-instantsearch-dom';

import styles from './style.css';

//import './App.css';


const searchClient = algoliasearch('M8VYFLBKJM', 'de67302e56ffb53902d15685a3cf67dd');
class App extends Component {
  render() {
    return (

      <InstantSearch indexName="cx_assignment" searchClient={searchClient}>
          <div>
            <ClearRefinements />
            <h2>Brands</h2>
            <RefinementList attribute="brand" />
            <Configure hitsPerPage={2} />
          </div>
       <header className={styles.header} >
       <img src="instant_search_logo@2x.png" />
       <SearchBox  translations={{placeholder:"Search for products"}} />
       </header>

            <Hits hitComponent={Hit} />
            {/* <Pagination /> */}

        </InstantSearch>

    );
  }
}

function Hit(props) {
  return (
    <div>
      <img src={props.hit.image}  alt={props.hit.name} />
      <div className="hit-name" style={{flexDirection:'row' }} >
         <div> <h3> Name </h3></div>
         <div>  <Highlight attribute="name" hit={props.hit} /></div>


      </div>
      {/* <div className="hit-description">
        <Highlight attribute="description" hit={props.hit} />
      </div> */}
      <div className="hit-price">${props.hit.price}</div>
    </div>
  );
}



export default App;
